import {
  ADD_INPUT,
  SET_LOADING,
  GET_INPUTS,
  DELETE_INPUT,
  EDIT_INPUT,
  ADD_EDITED_INPUTS,
  INPUTS_ERROR,
} from "./types";

const http = "http://localhost:7070/api/services/";
//Set loading
export const addEditedInputs = (id, task, price) => ({
  type: "ADD_EDITED_INPUTS",
  id,
  task,
  price,
});

// Get inputs from server
export const getInputs = () => async (dispatch) => {
  try {
    setLoading();

    const res = await fetch(`${http}`);
    console.log(res);
    const data = await res.json();
    dispatch({
      type: GET_INPUTS,
      payload: data,
    });
  } catch (err) {
    dispatch({
      type: INPUTS_ERROR,
      payload: err,
    });
  }
};

// Add new log
export const addInput = (name, price, description) => async (dispatch) => {
  try {
    setLoading();

    const res = await fetch("/logs", {
      method: "POST",
      body: JSON.stringify({name, price, description}),
      headers: {
        "Content-Type": "application/json",
      },
    });
    const data = await res.json();

    dispatch({
      type: ADD_INPUT,
      payload: data,
    });
  } catch (err) {
    dispatch({
      type: INPUTS_ERROR,
      payload: err.response.statusText,
    });
  }
};

// Delete log from server
export const deleteInput = (id) => async (dispatch) => {
  try {
    setLoading();

    await fetch(`${http}${id}`, {
      method: "DELETE",
    });

    dispatch({
      type: DELETE_INPUT,
      payload: id,
    });
  } catch (err) {
    dispatch({
      type: INPUTS_ERROR,
      payload: err.response.statusText,
    });
  }
};

// Update log on server
export const editInput = (id) => async (dispatch) => {
  try {
    setLoading();

    const res = await fetch(`//${id}`, {
      method: "PUT",
      body: JSON.stringify(),
      headers: {
        "Content-Type": "application/json",
      },
    });

    const data = await res.json();

    dispatch({
      type: EDIT_INPUT,
      payload: data,
    });
  } catch (err) {
    dispatch({
      type: INPUTS_ERROR,
      payload: err.response,
    });
  }
};

// Set loading to true
export const setLoading = () => {
  return {
    type: SET_LOADING,
  };
};
