import {
  ADD_INPUT,
  SET_LOADING,
  GET_INPUTS,
  REMOVE_INPUT,
  EDIT_INPUT,
  ADD_EDITED_INPUTS,
  HANDLE_FILTER,
} from "../actions/types";

const intialState = {
  list: null,
  editItem: {},
  editMode: false,
  loading: false,
};

export default (state = intialState, action) => {
  switch (action.type) {
    case GET_INPUTS:
      return {
        ...state,
        list: action.payload,
        loading: false,
      };

    case SET_LOADING:
      return {
        ...state,
        loading: true,
      };

    case ADD_INPUT:
      return {
        ...state,
        list: [
          ...state.list,
          {
            id: action.id,
            task: action.task,
            price: action.price,
          },
        ],
      };

    case REMOVE_INPUT:
      return {
        ...state,
        list: [...state.list.filter((input) => input.id !== action.id)],
      };

    case EDIT_INPUT:
      return {
        ...state,
        editMode: !state.editMode,
        editItem: state.list.filter((input) => input.id === action.id),
      };

    case ADD_EDITED_INPUTS:
      return {
        editMode: !state.editMode,
        editItem: {},
        list: [
          ...state.list.filter((input) => input.id !== action.id),
          {
            id: action.id,
            task: action.task,
            price: action.price,
          },
        ],
      };

    case LOGS_ERROR:
      console.error(action.payload);
      return {
        ...state,
        error: action.payload,
      };

    default:
      return state;
  }
};
