import React, {useState, useEffect} from "react";
import PropTypes from "prop-types";
import {connect} from "react-redux";
import {addInput, editInput} from "../../actions/inputActions";
import "./input-from.css";

const InputForm = ({addInput, editItem, editMode, editInput}) => {
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");

  useEffect(() => {
    if (editMode) {
      setPrice(editItem[0].price);
      setName(editItem[0].name);
      setDescription(editItem[0].description);
    }
    // eslint-disable-next-line
  }, [editMode]);

  const clearInputFields = () => {
    setPrice("");
    setName("");
    setDescription("");
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editMode) {
      addEditedInputs(editItem[0].id, name, price, description);
      clearInputFields();
    } else {
      addInput(name, price, description);
      clearInputFields();
    }
  };

  const handleCancel = (e) => {
    e.preventDefault();

    addEditedInputs(editItem[0].id, name, price, description);
    clearInputFields();
  };

  return (
    <form onSubmit={handleSubmit} className="form">
      <label>Вид ремонта</label>
      <input
        className="name-input"
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
        autoFocus
      />
      <label>Цена</label>
      <input
        className="price-input"
        type="number"
        value={price}
        onChange={(e) => setPrice(e.target.value)}
      />
      <label>Описание</label>
      <input
        className="description-input"
        type="text"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <button type="submit" className="save-btn">
        Save
      </button>
      {editMode ? (
        <button className="cancel-btn" onClick={handleCancel}>
          Cancel
        </button>
      ) : null}
    </form>
  );
};

InputForm.propTypes = {
  addInput: PropTypes.func.isRequired,
  editItem: PropTypes.func.isRequired,
  editMode: PropTypes.bool.isRequired,
};

const mapStateToProps = (state) => ({
  editItem: state.inputs.editItem,
  editMode: state.inputs.editMode,
});

export default connect(mapStateToProps, {addInput, addEditedInputs})(InputForm);
